mapper.select:
select
,
from xarea where id = #{id,jdbcType=INTEGER}

SELECT * from xarea x


        and x.battalion=#{battalion}


        AND x.detachment=#{detachment}


        AND x.station=#{station}


        AND x.leader=#{leader}


        AND x.type =#{grid}


        AND x.conment=#{type}

select s.*,x.*,st.* from xarea x,staff s ,xrelation xl,section st
where x.station = #{station}

    and x.battalion = #{battalion}


    and x.detachment = #{detachment}

and x.id = xl.xarea_id
and s.sys_staff_id = xl.staff_id
and s.sys_section_id = st.sys_section_id
GROUP BY s.sys_staff_id

select s.*,x.gps,x.battalion,st.* from xarea x,staff s ,xrelation xl,section st
where x.conment = #{conment}

    and x.battalion = #{battalion}


    and x.detachment = #{detachment}

and s.sys_section_id = st.sys_section_id
and x.id = xl.xarea_id
and s.sys_staff_id = xl.staff_id
GROUP BY s.sys_staff_id

select s.*,x.gps,x.battalion,st.* from xarea x,staff s ,xrelation xl,section st where x.conment != '3'
and x.battalion = #{battalion}
and (x.station = '固定岗' or conment = '4') and x.id = xl.xarea_id and s.sys_staff_id = xl.staff_id and s.sys_section_id = st.sys_section_id GROUP BY s.sys_staff_id

select s.*,x.battalion,st.* from xarea x,staff s ,xrelation xl,section st where x.conment != '3'
and x.battalion = #{battalion}
and ((((x.gridding like '%网格%' or x.type = '面') and x.gridding is not null and x.gridding not like '%级网格%') or x.station = '高速岗' or conment = '2') or (station != '固定岗' and station != '高峰岗' and station != '高速岗' and conment != '2' and conment != '4' and station != '快速岗' and station != '区域')) and x.id = xl.xarea_id and s.sys_staff_id = xl.staff_id and s.sys_section_id = st.sys_section_id GROUP BY s.sys_staff_id

select s.sys_staff_id from xarea x,staff s ,xrelation xl
where x.conment != '3'
and x.battalion = #{battalion}
and x.detachment = #{detachment}
and (x.station = '固定岗' or x.conment = '4'

    or x.station = #{station}
)
and x.id = xl.xarea_id
and s.sys_staff_id = xl.staff_id
GROUP BY s.sys_staff_id

select xl.staff_id from xarea x,staff s ,xrelation xl where (x.gridding like '%网格%' or x.type = '面') and x.gridding is not null and x.gridding not like '%级网格%'
and x.battalion = #{battalion}
and x.id = xl.xarea_id and s.sys_staff_id = xl.staff_id GROUP BY xl.staff_id

select xl.staff_id from xarea x,staff s ,xrelation xl where x.conment = '4'
and x.battalion = #{battalion}
and x.id = xl.xarea_id and s.sys_staff_id = xl.staff_id GROUP BY xl.staff_id

select id from xarea
where conment = '2'

    and battalion = #{battalion}

select count(*) from xarea
where station = #{station}

    and battalion = #{battalion}

select gridding from xarea where (gridding like '%网格%' or type = '面') and gridding is not null and gridding not like '%级网格%'
and battalion = #{battalion}
group by gridding

select count(*) from xarea
where conment = '4'

    and battalion = #{battalion}

select s.sys_staff_id from xarea x,staff s,xrelation xl where x.station != '固定岗' and x.station != '高峰岗' and x.station != '高速岗' and x.conment != '2' and x.conment != '4' and x.station != '快速岗' and x.station != '区域' and xl.xarea_id = x.id and xl.staff_id = s.sys_staff_id
and x.battalion = #{battalion}
and x.conment != 3
and x.conment = #{conment}
GROUP BY s.sys_staff_id

select s.sys_staff_id from xarea x,staff s ,xrelation xl
where x.conment = #{conment}
and x.battalion = #{battalion}
and x.detachment = #{detachment}
and x.id = xl.xarea_id
and s.sys_staff_id = xl.staff_id
GROUP BY  s.sys_staff_id

select x.detachment from xarea x,section st
where x.battalion = #{battalion}

    and x.conment != '3'


    and x.conment = #{conment}

and x.battalion = st.section_name
GROUP BY detachment ORDER BY st.sys_section_id

select id from xarea where station = #{station}
and battalion = #{battalion}
and name is not null and name != '' and detachment != '' and detachment is not null GROUP BY name,battalion

select s.sys_staff_id from xarea x,staff s,xrelation xl
where station = #{station}

    and battalion = #{battalion}


    and x.conment != 3


    and x.conment = #{conment}

AND  x.name is not NULL
and xl.xarea_id = x.id
and xl.staff_id = s.sys_staff_id
GROUP BY s.sys_staff_id

select s.sys_staff_id from xarea x,staff s,xrelation xl where x.conment = '3'
and battalion = #{battalion}
and (x.detachment like '%夜巡%' or x.gridding like '%夜巡%') and xl.xarea_id = x.id and xl.staff_id = s.sys_staff_id GROUP BY s.sys_staff_id

select x.* from xarea x,staff s,xrelation xl,section st where (x.station = '机动岗' or x.conment = '2' or x.station = '高速岗' or x.station = '快速岗') and xl.xarea_id = x.id and xl.staff_id = s.sys_staff_id and s.sys_section_id = st.sys_section_id
and x.conment = #{conment}
GROUP BY battalion

select s.*,x.*,st.* from xarea x,staff s ,xrelation xl,section st
where x.conment = '4' and x.id = xl.xarea_id and s.sys_staff_id = xl.staff_id

    and x.battalion = #{battalion}


    and x.detachment = #{detachment}

AND s.sys_section_id=st.sys_section_id GROUP BY xl.staff_id

select s.*,x.*,st.* from xarea x,staff s ,xrelation xl,section st
where (x.gridding like '%网格%' or x.type = '面') and x.gridding is not null and x.gridding not like '%级网格%'

    and x.battalion = #{battalion}


    and x.detachment = #{detachment}

AND s.sys_section_id=st.sys_section_id
and x.id = xl.xarea_id and s.sys_staff_id = xl.staff_id GROUP BY xl.staff_id

select s.*,x.*,st.section_name from xarea x,staff s,xrelation xl,section st
where  xl.xarea_id = x.id and  s.sys_section_id=st.sys_section_id

    and station = #{station}


    and battalion = #{battalion}


    and detachment = #{detachment}


    and x.conment != 3


    and x.conment = #{conment}

AND  x.name is not NULL
and xl.staff_id = s.sys_staff_id
GROUP BY s.sys_staff_id

select s.*,x.gps,x.*,st.section_name from xarea x,staff s,xrelation xl ,section st where x.station != '固定岗' and x.station != '高峰岗' AND s.sys_section_id=st.sys_section_id and x.station != '高速岗' and x.conment != '2' and x.conment != '4' and x.station != '快速岗' and x.station != '区域' and xl.xarea_id = x.id and xl.staff_id = s.sys_staff_id
and x.battalion = #{battalion}
and x.detachment = #{detachment}
and x.conment != 3 GROUP BY s.sys_staff_id

SELECT f.*,a.*,s.section_name FROM patrolrecord p,staff f,section s,xarea a,xrelation x WHERE a.id=x.xarea_id
and a.battalion = #{battalion}
and a.detachment = #{detachment}
and (a.detachment = null or a.detachment = '')
AND f.sys_staff_id=x.staff_id AND f.sys_section_id=s.sys_section_id and f.sys_staff_id=p.sys_staff_id and to_days(p.patrol_record_begintime) = to_days(now()) AND p.patrol_record_endtime is null GROUP BY sys_staff_id

select s.*,x.*,st.section_name from xarea x,staff s,xrelation xl,section st where x.conment = '3'
and battalion = #{battalion}
and x.detachment = #{detachment}
and (a.detachment = null or a.detachment = '')
AND st.sys_section_id=s.sys_section_id and (x.detachment like '%夜巡%' or x.gridding like '%夜巡%') and xl.xarea_id = x.id and xl.staff_id = s.sys_staff_id GROUP BY s.sys_staff_id

select s.*,x.*,st.section_name from xarea x,staff s,xrelation xl,section st where st.sys_section_id=s.sys_section_id and x.conment = '3' and xl.xarea_id = x.id
and battalion = #{battalion}
and station = #{station}
and x.detachment = #{detachment}
and (a.detachment = null or a.detachment = '')
and xl.staff_id = s.sys_staff_id GROUP BY s.sys_staff_id

select s.*,x.*,st.section_name from xarea x,staff s,xrelation xl,section st where x.station != '固定岗' and x.station != '高峰岗' and x.station != '高速岗' and x.conment != '2' and x.conment != '4' and x.station != '快速岗' and x.station != '区域' and xl.xarea_id = x.id
and battalion = #{battalion}
and x.detachment = #{detachment}
AND s.sys_section_id=st.sys_section_id and xl.staff_id = s.sys_staff_id and x.conment = '3' GROUP BY s.sys_staff_id

SELECT * from xarea where

    station in ('九主','六块','示范区')


    station=#{station}

SELECT f.*,x.* from xarea x,xrelation xl,staff f WHERE f.sys_staff_id=xl.staff_id and x.id = xl.xarea_id AND x.name LIKE CONCAT('%',#{name},'%')
and battalion = #{battalion}
GROUP BY xl.staff_id
